import './url.css';
import React, {useState} from 'react';


function URL() {

    const [text, setText] = useState('');
    const [shortUrl, setShortUrl] = useState('');


    const handleText = (e)=>{
        setText(e.target.value)
    }

    const submitUrl = ()=>{
        const options = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'sk_ghOUKJBkaDe0wj2O' // Replace with your API key
            },
            body: JSON.stringify({
                originalURL: text,
                domain: 'cww4.short.gy' // Replace with your domain
            })
        };

        fetch('https://api.short.io/links', options)
            .then(response => response.json())
            .then(data => {
                setShortUrl(data.shortUrl); // Assuming 'shortUrl' is the key in the response
                console.log(data);
            })
            .catch(error => {
                console.error('Error:', error);
            });
    }


    return (
        <>
            <div className='main-container'>
                <div className='input-container'>
                    <label>URL LINK HERE</label>
                    <textarea
                        placeholder='add url here'
                        value={text}
                        onChange={handleText}
                    ></textarea>
                    <p>{text}</p>
                </div>
                <button
                    onClick={submitUrl}
                >
                    Shorten URL
                </button>

            </div>
        </>
    );
}

export default URL;
